    <body class="font-sans antialiased dark:bg-black dark:text-white/50">
        <!-- Toast Component with Alpine.js -->
        <div x-data="{ show: false, type: '', message: '' }"
             x-init="
                @if (session('toast'))
                    show = true;
                    type = '{{ session('toast.type') }}';
                    message = '{{ session('toast.message') }}';
                    setTimeout(() => { show = false; }, 4000);
                @endif
             "
             x-show="show"
             x-transition:enter="transition ease-out duration-300"
             x-transition:leave="transition ease-in duration-300"
             class="fixed bottom-5 right-5" style="top: 20px;right: 20px;color: black;font-weight: 900;">
            <div class="flex items-center p-4 rounded-lg shadow-lg bg-white border-l-4"
                 :class="{
                     'border-green-500': type === 'success',
                     'border-red-500': type === 'error',
                     'border-blue-500': type === 'info'
                 }">
                <span x-text="message"></span>
            </div>
        </div>
        <div class="bg-gray-50 text-black/50 dark:bg-black dark:text-white/50">
            <img id="background" class="absolute -left-20 top-0 max-w-[877px]" src="https://laravel.com/assets/img/welcome/background.svg" alt="Laravel background" />
            <div class="relative min-h-screen flex flex-col items-center justify-center selection:bg-[#FF2D20] selection:text-white">
                <div class="relative w-full max-w-2xl px-6 lg:max-w-7xl">
                    <header class="grid grid-cols-2 items-center gap-2 py-10 lg:grid-cols-3">
                        <div class="flex lg:justify-center lg:col-start-2">
                            <svg class="h-12 w-auto text-white lg:h-16 lg:text-[#FF2D20]" xmlns="http://www.w3.org/2000/svg" id="Layer_2" data-name="Layer 2" viewBox="0 0 172.71 58.78">
                                <defs>
                                    <style>
                                        .cls-1 {
                                            fill: #fff;
                                        }
                                    </style>
                                </defs>
